import React, { useState, useEffect }from 'react';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import { Calendar, momentLocalizer} from 'react-big-calendar';
import moment from 'moment'
import '../index.css';
import { Modal } from "react-bootstrap";


const CalendarBook = () => {
  const [start, setStart] = React.useState(null);
  const [end, setEnd] = React.useState(null);
  const [reason, setReason] = React.useState("");
  const Globallocalizer = momentLocalizer(moment);

  const [trainings, setTrainings] = useState([]);

  useEffect(() => {
    getTrainings()
  });

var data;
  const getTrainings = () => {
    fetch('https://customerrest.herokuapp.com/gettrainings')
      .then(response => response.json())
      .then(data => {
       // console.log(data)
        var data = setTrainings(data.map((training) =>(
          {
            title: training.activity,
            start: moment.utc(training.data)._d,
            end: moment.utc(training.date).add(trainings.duration, 'minutes')._d,
            resource: training.customer.firstname
          })
        ))
        //console.log(data)
  })
      .catch(err => console.error(err))
  }
  // const dummyEvents = [
  //   {
  //     allDay: false,
  //     endDate: new Date('December 10, 2022 11:13:00'),
  //     startDate: new Date('December 09, 2022 11:13:00'),
  //     title: 'hi',
  //   },
  //   {
  //     allDay: true,
  //     endDate: new Date('December 09, 2022 11:13:00'),
  //     startDate: new Date('December 09, 2022 11:13:00'),
  //     title: 'All Day Event',
  //   },
  //   ];
 
  
    const minTime = new Date();
    minTime.setHours(6,0,0);
    const maxTime = new Date();
    maxTime.setHours(22,0,0);
    const [events, setEvents] = useState([]);

    const handleSelect = (event) => {
     // setShowAddModal(true);
      let { start, end, reason } = event;
      start = new Date(start);
      end = new Date(end);
      const data = { start, end, reason };
      setStart(data.start);
      setEnd(data.end);
      setReason(data.reason);
    };
  
  const localizer = momentLocalizer(moment);
  useEffect(() => {
    async function fetchEvents() {
      const res = await fetch('https://private-37dacc-cfcalendar.apiary-mock.com/mentors/1/agenda');
      res
        .json()
        .then(res => 
          {
            let todos = res.mentor.name;
            console.log(todos);
            setEvents(res.calendar)

          })
        .catch(err => console.log(err));
    }
    fetchEvents();
  }, []);
  const event = events.map((slot, index) => {
    return  {
      'allDay': false,
      'start': moment.utc(slot.date_time).toDate(),
      'end': moment.utc(slot.date_time).add(60, 'minutes').toDate(),
      'reason': ''
    }
  })
          // console.log(res.mentor.name)
 
  return (
  
 <div className="sampleBook">  
 
   <h2 className='text-center text-primary pb-3 pt-2'> Book A Mentor  </h2>
   <Calendar
        localizer={Globallocalizer}
        events={trainings}
        resourceTitleAccessor='resource'
        startAccessor='start'
        endAccessor='end'
        views={['month', 'week', 'day']}
        style={{height: 450}}
        />
      <Calendar
        events ={event}
        formats={{ dateFormat: 'yyyy, mm, dd' }}
          step={60}
          localizer={localizer}
          style={{ height: '100vh' }}
          min = {minTime}
          max = {maxTime}
          views={['week']}
          defaultView='week'
          selectable={true}
          startAccessor='start'
          endAccessor='end'
          onSelectSlot={handleSelect}
        style={{ height: 500 }}
      />
</div>
  )
}

export default CalendarBook;
