import React, { useState, useEffect }from 'react';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import { Calendar, momentLocalizer} from 'react-big-calendar';
import moment from 'moment'
import '../index.css';


const CalendarBook = () => {
  const initialEvents = [
    {
        id: 0,
        title: "All Day Event very long title",
        allDay: true,
        start: new Date(2015, 3, 0),
        end: new Date(2015, 3, 1)
    },
    {
        id: 1,
        title: "Long Event",
        start: new Date(2015, 3, 7),
        end: new Date(2015, 3, 10)
    },
];
  const [events, setEvents] = useState(initialEvents);


  let todos;
  const localizer = momentLocalizer(moment);
          useEffect(() => {
            async function fetchEvents(){ 
            fetch('https://private-37dacc-cfcalendar.apiary-mock.com/mentors/1/agenda')
            .then(response => response.json())
            .then(data => 
              {
                let todos = data.mentor.name;
                console.log(data);
                setEvents(data.calendar)

              })
              .catch(err => console.log(err));
            }
            // Update the document title using the browser API
            fetchEvents();
          }, []);


          // console.log(res.mentor.name)
 
  return (
  
 <div className="sampleBook">  
   <h2 className='text-center text-primary pb-3 pt-2'> Book A Mentor  </h2>
      <Calendar
          events ={events}
          formats={{ dateFormat: 'yyyy, mm, dd' }}
          step={60}
          localizer={localizer}
          style={{ height: '100vh' }}
          // min = {minTime}
          // max = {maxTime}
          views={['week']}
          defaultView='week'
          selectable={true}
          startAccessor="startDate"
          endAccessor="endDate"
        style={{ height: 500 }}
      />
</div>
  )
}

export default CalendarBook;
