        style={{height: 450}}
        /> */}
      <Calendar
          events ={eventList}
          formats={{ dateFormat: 'yyyy, mm, dd' }}
          step={60}
          localizer={localizer}
          style={{ height: '100vh' }}
          min = {minTime}
          max = {maxTime}
          views={['week']}
          defaultView='week'
          selectable={true}
          startAccessor="startDate"
          endAccessor="endDate"
        style={{ height: 500 }}
      />
</div>
  )
}

export default CalendarBook;
